mapper.update:
update Attendance set
	attendance_time=#{attendance_time},
	attendance_state=#{attendance_state}
where
	student_id= #{student_id} and attendance_date=#{attendance_date}

update Attendance set
	leaving_time=#{leaving_time},
	attendance_state=#{attendance_state}
where
	student_id= #{student_id} and attendance_date=#{attendance_date}
